#!/bin/bash

clear
base64 -d <<< ""

volume=1
if [ "$(whoami)" == "root" ]; then
	let volume++
fi

cd ~/Downloads
yamato=yamato.mp3
if [ ! -f "$yamato" ]; then
	wget "http://67.159.62.2/anime_ost/uchu-senkan-yamato-makkana-scarf-op-1/msknfenn/01.%20Uchuu%20Senkan%20Yamato.mp3" -O "$yamato"
fi

play -v $volume $yamato
